sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		var rightnow = new GlideDateTime();
		var testdatetime = new GlideDateTime(strDate);

		if(testdatetime.before(rightnow)){
			return true;
		}
		else{
			return false;
		}
	},
	isDateToday: function(strDate){
		var rightnow = new GlideDateTime();
		var testdatetime = new GlideDateTime(strDate);
		// or use today == testdate, please note to not use '===', it will compare whether the 2 objects refer to the same object, other than those value

		var today = rightnow.getLocalDate();
		var testdate = testdatetime.getLocalDate();

		if(today.equals(testdate)){
			return true;
		}
		else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
